monitor_all <- function(data, begin_date, house_width = "1", days, save_path) {
  pptsdm::fid_monitor(data, begin_date, station_type = "nedap", save_path)
  pptsdm::station_monitor(data, begin_date, station_type = "nedap", save_path)
  pptsdm::table_monitor(data, house_width, days, ref_date = Sys.Date(), save_path)
}
